    private void convertBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_convertBtnActionPerformed
        // TODO add your handling code here:

        String checkifEmpty = convertTxt.getText();

        if (checkifEmpty.length() == 0) {
                JOptionPane.showMessageDialog(this, "Textbox cannot be empty.", "Error", JOptionPane.ERROR_MESSAGE);
            }
        
        else {
        try {

        int temp = (int)((Double.parseDouble(convertTxt.getText())) * 1.8 + 32);
        convertTxt.setText("");
        convertTxt.setText(Integer.toString(temp) + " ยบ Fahrenheit");
        }

        catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Invalid number.\n Clearing textbox now.", "Error", JOptionPane.ERROR_MESSAGE);
            convertTxt.setText("");
            }
        }
    }//GEN-LAST:event_convertBtnActionPerformed

